window.calcprice=function(){let e;switch(document.getElementById("land").value){case"agricultural":default:e=1e3;break;case"dwelling":e=1500;break;case"historica":e=2e3}const t=document.getElementById("shirina").value,l=document.getElementById("dlina").value;if(""===t)alert("Вы не указали ширину");else if(""===l)alert("Вы не указали длину");else{const n=parseFloat(t)*parseFloat(l);document.getElementById("ploschad").innerHTML=`Площадь равна: ${n} кв. м.`;const a=n*e;document.getElementById("stoimost").innerHTML=`Стоимость равна: ${a} грн.`}};